<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class MY_Controller extends CI_Controller{    public function __construct()    {        parent::__construct();    }    protected function RenderView($path , $data=null){        ///----- Get data json file -----        $data["setting"] =  array(            "alt"       =>  "ข้อความ รูป",            "title"     =>  "ข้อความ ลิงค์"        );        $this->load->view("assets/header" , $data);        $this->load->view("assets/header_boby"      , $data);        $this->load->view($path , $data);        $this->load->view("assets/footer"      , $data);    }    protected function RenderViewAdmin($path , $data=null){        $this->load->view("panels/assets/header_html", $data);        $this->load->view("panels/assets/header", $data);        $this->load->view("panels/" . $path, $data);        $this->load->view("panels/assets/footer", $data);    }}?>