<?phpclass Date_model extends CI_Model{    function add_date($givendate, $day = 0, $mth = 0, $yr = 0)    {        $cd = strtotime($givendate);        $newdate = date('Y-m-d', mktime(date('h', $cd),            date('i', $cd), date('s', $cd), date('m', $cd) + $mth,            date('d', $cd) + $day, date('Y', $cd) + $yr));        return $newdate;    }    function minus_date($givendate, $day = 0, $mth = 0, $yr = 0)    {        $cd = strtotime($givendate);        $newdate = date('Y-m-d', mktime(date('h', $cd),            date('i', $cd), date('s', $cd), date('m', $cd) + $mth,            date('d', $cd) - $day, date('Y', $cd) + $yr));        return $newdate;    }    function thai_date($time){        $thai_day_arr=array("อาทิตย์","จันทร์","อังคาร","พุธ","พฤหัสบดี","ศุกร์","เสาร์");        $thai_month_arr=array(            "0"=>"",            "1"=>"มกราคม",            "2"=>"กุมภาพันธ์",            "3"=>"มีนาคม",            "4"=>"เมษายน",            "5"=>"พฤษภาคม",            "6"=>"มิถุนายน",            "7"=>"กรกฎาคม",            "8"=>"สิงหาคม",            "9"=>"กันยายน",            "10"=>"ตุลาคม",            "11"=>"พฤศจิกายน",            "12"=>"ธันวาคม"        );//        global $thai_day_arr,$thai_month_arr;        $thai_date_return="วัน".$thai_day_arr[date("w",$time)];        $thai_date_return.=	" ที่ ".date("j",$time);        $thai_date_return.= " ".$thai_month_arr[date("n",$time)];        $thai_date_return.=	" ".(date("Yํ",$time)+543);        $thai_date_return.=	"  ".date("H:i",$time)." น.";        return $thai_date_return;    }    function month($time){        $thai_day_arr=array("อาทิตย์","จันทร์","อังคาร","พุธ","พฤหัสบดี","ศุกร์","เสาร์");        $thai_month_arr=array(            "0"=>"",            "1"=>"ม.ค.",            "2"=>"ก.พ",            "3"=>"มี.ค.",            "4"=>"เม.ย.",            "5"=>"พ.ค.",            "6"=>"มิ.ย",            "7"=>"ก.ค.",            "8"=>"ส.ค.",            "9"=>"ก.ย.",            "10"=>"ต.ค.",            "11"=>"พ.ย.",            "12"=>"ธ.ค."        );//        global $thai_day_arr,$thai_month_arr;//        $thai_date_return="วัน".$thai_day_arr[date("w",$time)];        $thai_date_return=	" ".date("j",$time);        $thai_date_return= " ".$thai_month_arr[date("n",$time)];//        $thai_date_return.=	" ".(date("Yํ ",$time)+543);//        $thai_date_return.=	"  ".date("H:i",$time)." น.";        return $thai_date_return;    }    function thai_date_shot($time){        $thai_day_arr=array("อา","จ","อ","พ","พฤ","ศ","ส");        $thai_month_arr=array(            "0"=>"",            "1"=>"ม.ค.",            "2"=>"ก.พ",            "3"=>"มี.ค.",            "4"=>"เม.ย.",            "5"=>"พ.ค.",            "6"=>"มิ.ย",            "7"=>"ก.ค.",            "8"=>"ส.ค.",            "9"=>"ก.ย.",            "10"=>"ต.ค.",            "11"=>"พ.ย.",            "12"=>"ธ.ค."        );//        global $thai_day_arr,$thai_month_arr;        $thai_date_return="วัน".$thai_day_arr[date("w",$time)];        $thai_date_return=	" ".date("j",$time);        $thai_date_return.= " ".$thai_month_arr[date("n",$time)];        $thai_date_return.=	" ".(date("y ",$time)+43);        $thai_date_return.=	"  ".date("H:i",$time)." น.";        return $thai_date_return;    }    function thai_date_day($time){        $thai_day_arr=array("อาทิตย์","จันทร์","อังคาร","พุธ","พฤหัสบดี","ศุกร์","เสาร์");        $thai_month_arr=array(            "0"=>"",            "1"=>"มกราคม",            "2"=>"กุมภาพันธ์",            "3"=>"มีนาคม",            "4"=>"เมษายน",            "5"=>"พฤษภาคม",            "6"=>"มิถุนายน",            "7"=>"กรกฎาคม",            "8"=>"สิงหาคม",            "9"=>"กันยายน",            "10"=>"ตุลาคม",            "11"=>"พฤศจิกายน",            "12"=>"ธันวาคม"        );//        global $thai_day_arr,$thai_month_arr;        $thai_date_return=$thai_day_arr[date("w",$time)];//        $thai_date_return.=	" ที่ ".date("j",$time);//        $thai_date_return.= " ".$thai_month_arr[date("n",$time)];//        $thai_date_return.=	" ".(date("Yํ",$time)+543);//        $thai_date_return.=	"  ".date("H:i",$time)." น.";        return $thai_date_return;    }    function Sys_StrDtmFormWeek($year, $week ){        if ($week <=0)        {            $year=$year-1;            $week = 52;        }        $from = strtotime("{$year}-W{$week}-0");        $Start = mktime(0,0,0,date("m", $from),date("d", $from),date("Y", $from));        $Stop = mktime(23,59,59,date("m", $from),date("d", $from),date("Y", $from))+(86400*6);        $arrDateStrToStpWeek = array('Start'=>$Start,'Stop'=>$Stop);        return $arrDateStrToStpWeek;    }}