{vb:data customBbcode, editor, fetchCustomBbcode}
{vb:data editorPhrases, phrase, fetchByGroup, "ckeditor"}
{vb:data limitInfo, editor, fetchContentEntryLimits, {vb:raw channelInfo},  {vb:raw conversation}}
<script type="text/javascript">
(function(){
	<vb:literal>
		window.vBulletin = window.vBulletin || {};
		window.vBulletin.contentEntryBox = window.vBulletin.contentEntryBox || {};
	</vb:literal>
	<vb:comment>Use of vb:raw here is intentional, because we don't want to mess up the
		custom bbcodes/phrases, and because any user input would come from users with
		admin level access whom we're going to have to trust.
		NOTE: we loop to create these two objects manually, since json_encode can't
		handle non-UTF-8 data.
	</vb:comment>
	<vb:if condition="$customBbcode">
		window.vBulletin.customBbcode = {'': ''<vb:each from="customBbcode" key="bbcodetag" value="bbcode">, '{vb:escapejs {vb:raw bbcodetag}}': {'title': '{vb:escapejs {vb:raw bbcode.title}}', 'twoparams': '{vb:escapejs {vb:raw bbcode.twoparams}}', 'buttonimage': '{vb:escapejs {vb:raw bbcode.buttonimage}}'}</vb:each>};
	</vb:if>
	<vb:if condition="$editorPhrases">
		window.vBulletin.ckeditorPhrases = {'':''<vb:each from="editorPhrases" key="phrase" value="value">, '{vb:escapejs {vb:raw phrase}}' : '{vb:escapejs {vb:raw value}}'</vb:each>};
	</vb:if>
	<vb:comment>End use of intentional vb:raw</vb:comment>
	pageData.pageView = "{vb:var page.view}";
	pageData.ckeditorCss = "{vb:csspath css_editor.css}";
	<vb:if condition="!empty($channelInfo['channeltype'])">
		pageData.channelType = "{vb:var channelInfo.channeltype}";
	</vb:if>
	<vb:if condition="!empty($attachLimit)">
		vBulletin.contentEntryBox.ATTACHLIMIT = Number("{vb:var attachLimit}");
	</vb:if>
	<vb:comment>todo: usermentions limit (UGP) </vb:comment>
	vBulletin.contentEntryBox.bbcode_enabled = {};
	<vb:if condition="!empty($limitInfo['enabled']['bbcode_user'])">
		vBulletin.contentEntryBox.bbcode_enabled.user = true;
	<vb:else />
		vBulletin.contentEntryBox.bbcode_enabled.user = false;
	</vb:if>

})();
</script>

