{vb:set totalComments, {vb:raw conversation.textcount}}
{vb:set modPerms, {vb:raw conversation.moderatorperms}}
<vb:if condition="$enableInlineMod AND $modPerms['canmoderateposts'] > 0">
	{vb:set totalComments, {vb:math {vb:var totalComments} + {vb:raw conversation.totalunpubcount}}}
</vb:if>

<div class="b-comments js-comments {vb:if "!empty($parentNodeIsBlog)", ' b-comments--blog js-comments--blog'} {vb:if "!empty($parentNodeIsArticle)", ' b-comments--article js-comments--article'} h-margin-bottom-xl h-clearfix<vb:if condition="!$totalComments"> h-hide</vb:if>">
	<vb:if condition="empty($commentsPerPage)">
		{vb:set commentsPerPage, {vb:raw widgetConfig.commentsPerPage}}
	</vb:if>
	<vb:if condition="empty($commentsPerPage)">
		{vb:set commentsPerPage, 25}
	</vb:if>
	<vb:if condition="empty($initialCommentsPerPage)">
		{vb:set initialCommentsPerPage, {vb:raw widgetConfig.initialCommentsPerPage}}
	</vb:if>
	<vb:if condition="empty($initialCommentsPerPage)">
		{vb:set initialCommentsPerPage, 3}
	</vb:if>

	{vb:set pagenum, 1}
	{vb:set scrollToCommentFlag, 0}
	{vb:set hasComments, 0}
	{vb:set sortOption.created, 'DESC'}
	{vb:set options.sort, {vb:raw sortOption}}
	{vb:set options.nolimit, 1}

	<vb:if condition="isset($commentId) AND intval($commentId) > 0">
		{vb:data pagenum, search, getPageNumber, {vb:raw commentId}, {vb:raw conversation}, {vb:raw commentsPerPage}, 1, {vb:raw sortOption.created}, 0}
		<vb:if condition="$pagenum > 0">
			{vb:set initialCommentsPerPage, {vb:raw commentsPerPage}}
			{vb:set scrollToCommentFlag, 1}
		<vb:else />
			{vb:set pagenum, 1}
		</vb:if>
	</vb:if>
	<vb:if condition="($totalPages = ceil($totalComments / $initialCommentsPerPage)) > 0">
		{vb:data nodes, node, listNodeContent, {vb:raw conversation.nodeid}, {vb:raw pagenum}, {vb:raw initialCommentsPerPage}, 1, null, {vb:raw options}}
		<vb:if condition="empty($nodes['errors']) AND ($nodeCount = count($nodes)) > 0">
			{vb:set hasComments, 1}
			{vb:set commentIndex, {vb:raw totalComments}}
			<vb:if condition="$scrollToCommentFlag AND $pagenum > 1">
				{vb:set commentIndex, {vb:math {vb:var commentIndex} - {vb:math {vb:var pagenum} * {vb:var commentsPerPage}} + {vb:var commentsPerPage}}}
			<vb:else />
				{vb:set commentIndex, {vb:math {vb:var commentIndex} - {vb:var nodeCount} + 1}}
			</vb:if>




			<div class="b-comments__header js-comments__header OLD__top b-media h-padding-vert-m h-padding-horiz-l">
				<div class="b-media__img--rev js-comments__pagination<vb:if condition="$totalComments <= $initialCommentsPerPage"> h-hide</vb:if>">
					<label>
						 <vb:if condition="$pagenum > 1 OR ($pagenum == 1 AND $nodeCount > 1)">
							{vb:set topIndexFrom, {vb:raw commentIndex}}
							{vb:set topIndexTo, {vb:math {vb:var topIndexFrom} + {vb:var commentsPerPage} - 1}}
							<vb:if condition="$commentsPerPage > $nodeCount">
								{vb:set topIndexTo, {vb:math {vb:var topIndexFrom} + {vb:var nodeCount} - 1}}
							</vb:if>
							<vb:if condition="$topIndexTo > $totalComments">
								{vb:set topIndexTo, {vb:raw totalComments}}
							</vb:if>
							<vb:if condition="$topIndexFrom == $topIndexTo">
								{vb:number {vb:raw topIndexFrom}}
							<vb:else />
								{vb:number {vb:raw topIndexFrom}} - {vb:number {vb:raw topIndexTo}}
							</vb:if>
						</vb:if>
					</label>
					<span class="b-comments__pagination--up js-comments__page--prev h-margin-left-s OLD__icon<vb:if condition="$pagenum == $totalPages"> h-hide-imp</vb:if>" title="{vb:phrase see_previous_comments}"<vb:if condition="$scrollToCommentFlag AND $pagenum < $totalPages"> data-page-num="{vb:math {vb:var pagenum} + 1}"</vb:if>><span class="b-icon b-icon__arrow-up-m--default"></span></span>
				</div>
				<div class="js-comments__total OLD__comment-total h-margin-left-l OLD__h-left b-media__body">
					<span class="b-icon b-icon__speech-single h-margin-right-s"></span>
					<label class="js-comments__total-text h-align-top">
						<vb:if condition="$totalComments > 1">
							{vb:rawphrase x_comments_lower, {vb:number {vb:raw totalComments}}}
						<vb:else />
							{vb:rawphrase x_comment, {vb:number {vb:raw totalComments}}}
						</vb:if>
					</label>
				</div>
			</div>




			<vb:comment>
				<vb:if condition="$scrollToCommentFlag AND $nodeCount > 1">
				Ugly hack but the nodes need to be displayed in reversed order.
				The nodes should always be fetched in descending order but later when displaying (in certain condition such as this), they need to be in ascending order.
				</vb:if>
			</vb:comment>


			<vb:if condition="$nodes = array_reverse($nodes, true)"></vb:if>
			{vb:set nodeSchemaInfo.itemprop, comment}
			{vb:set nodeSchemaInfo.itemscope, 1}
			{vb:set nodeSchemaInfo.itemtype, http://schema.org/UserComments}


			<ul class="b-comments__comments js-comments__comments h-left" {vb:schema {vb:raw nodeSchemaInfo}}>

				{vb:set userIds, {vb:php array}}
				{vb:set userAvatarIds, {vb:php array}}
				<vb:each from="nodes" value="node">
					{vb:set num_elem, {vb:php array_push, {vb:raw userIds}, {vb:raw node.content.deleteuserid}}}
					{vb:set num_elem, {vb:php array_push, {vb:raw userAvatarIds}, {vb:raw node.content.userid}}}
				</vb:each>
				{vb:data usersInfo, user, fetchUsernames, {vb:raw userIds}}
				{vb:data userAvatars, user, fetchAvatars, {vb:raw userAvatarIds}, 1}

				<vb:each from="nodes" value="node">
					<vb:if condition="$node['content']['unpublishdate']">
						{vb:set hidePostIndex, 1}
					<vb:else />
						{vb:set hidePostIndex, 0}
					</vb:if>

					<vb:if condition="empty($node['content']['deleteuserid']) OR empty($usersInfo[$node['content']['deleteuserid']]) OR !($deleteUserInfo = $usersInfo[$node['content']['deleteuserid']])">
						{vb:set deleteUserInfo, 0}
					</vb:if>

					<vb:if condition="empty($userAvatars[$node['userid']]) OR !($userAvatar = $userAvatars[$node['userid']])">
						{vb:set userAvatar, 0}
					</vb:if>


					{vb:template conversation_comment_item, conversation={vb:raw node.content}, commentIndex={vb:raw commentIndex}, postIndex={vb:raw postIndex}, pagingInfo={vb:raw pagingInfo}, hidePostIndex={vb:raw hidePostIndex}, enableInlineMod={vb:raw enableInlineMod}, parentNodeIsBlog={vb:raw parentNodeIsBlog}, parentNodeIsArticle={vb:raw parentNodeIsArticle}, deleteUserInfo={vb:raw deleteUserInfo}, avatar={vb:raw userAvatar}}

					{vb:set commentIndex, {vb:math {vb:var commentIndex} + 1}}
				</vb:each>

			</ul>


			<div class="b-comments__footer js-comments__footer b-media OLD__bottom h-padding-vert-m h-padding-horiz-l h-clear<vb:if condition="!$scrollToCommentFlag OR $pagenum == 1"> h-hide</vb:if>">
				<div class="b-media__img--rev js-comments__pagination">
					<label>
						<vb:if condition="$scrollToCommentFlag AND $pagenum > 1">
							{vb:set bottomIndexFrom, {vb:math {vb:var topIndexTo} + 1}}
							{vb:set bottomIndexTo, {vb:math {vb:var bottomIndexFrom} + {vb:var commentsPerPage} - 1}}
							<vb:if condition="$bottomIndexTo > $totalComments">
								{vb:set bottomIndexTo, {vb:raw totalComments}}
							</vb:if>
							{vb:number {vb:raw bottomIndexFrom}} - {vb:number {vb:raw bottomIndexTo}}
						</vb:if>
					</label>
					<span class="b-comments__pagination--down js-comments__page--next h-margin-left-s OLD__icon" title="{vb:phrase see_next_comments}"<vb:if condition="isset($bottomIndexTo) AND $pagenum > 1"> data-page-num="{vb:math {vb:var pagenum} - 1}"</vb:if>><span class="b-icon b-icon__arrow-down-m--default"></span></span>
				</div>
			</div>

		</vb:if>
	</vb:if>

	<vb:if condition="!$hasComments">
		<div class="b-comments__header js-comments__header OLD__top b-media h-padding-vert-m h-padding-horiz-l h-hide">
			<div class="js-comments__total OLD__comment-total h-margin-left-l OLD__h-left b-media__body">
				<span class="b-icon b-icon__speech-single h-margin-right-s"></span>
				<label class="js-comments__total-text h-align-top"></label>
			</div>
			<div class="b-media__img--rev js-comments__pagination h-hide">
				<label></label>
				<span class="b-comments__pagination--up js-comments__page--prev h-margin-left-s OLD__icon" title="{vb:phrase see_previous_comments}"><span class="b-icon b-icon__arrow-up-m--default"></span></span>
			</div>
		</div>
		<ul class="b-comments__comments js-comments__comments h-left"></ul>
		<div class="b-comments__footer js-comments__footer b-media OLD__bottom h-padding-vert-m h-padding-horiz-l h-clear h-hide">
			<div class="b-media__img--rev js-comments__pagination">
				<label></label>
				<span class="b-comments__pagination--down js-comments__page--next h-margin-left-s OLD__icon" title="{vb:phrase see_next_comments}"><span class="b-icon b-icon__arrow-down-m--default"></span></span>
			</div>
		</div>
	</vb:if>
</div>

