{vb:template display_contenttype_threadview_footer, conversation={vb:raw conversation}, contenttype=link, conversationtype=reply, showInlineMod={vb:raw showInlineMod}, postIndex={vb:raw postIndex}, pagingInfo={vb:raw pagingInfo}, commentId={vb:raw commentId}, commentsPerPage={vb:raw commentsPerPage}, footerControls={vb:raw footerControls}, showNoControls={vb:raw showNoControls}, adlocation={vb:raw adlocation}}

