{vb:set enableInlineMod, 0}
{vb:set showNode, 1}
<vb:if condition="!empty($canmoderate) AND !empty($topic['permissions']) AND $topic['permissions']['canmoderate']">
	{vb:set enableInlineMod, 1}
</vb:if>

