{vb:data showHv, hv, fetchRequireHvcheck, {vb:raw action}}

<vb:if condition="$showHv">
	{vb:data type, hv, fetchHvType}
	<vb:if condition="empty($noToken)">
		{vb:data token, hv, generateToken}
	<vb:else />
		{vb:set token.hash, ''}
	</vb:if>
	<vb:if condition="empty($var_prefix)">
		{vb:set var_prefix, 'humanverify'}
	</vb:if>
	<vb:if condition="$type == 'Image'">
		{vb:template humanverify_image, hash={vb:raw token.hash}, var_prefix={vb:raw var_prefix}}
	<vb:elseif condition="$type == 'Question'" />
		{vb:template humanverify_question, hash={vb:raw token.hash}, var_prefix={vb:raw var_prefix}}
	<vb:elseif condition="$type == 'Recaptcha'" />
		{vb:template humanverify_recaptcha, hash={vb:raw token.hash}, var_prefix={vb:raw var_prefix}}
	</vb:if>
</vb:if>

{vb:data data, hv, fetchHvRecaptcha}
<div class="form_row humanverify humanverify_recaptcha">
	<label class="label_column">{vb:rawphrase image_verification}</label>
	<div class="field_column">
		<input class="hash" type="hidden" name="{vb:raw var_prefix}[hash]" value="{vb:raw hash}" />
		<div id="recaptcha_div"></div>
	</div>
</div>
<script type="text/javascript">

	var RecaptchaOptions = {
		theme : '{vb:raw data.theme}'
		<vb:if condition="$humanverify['langcode']">,lang : '{vb:raw data.langcode}'</vb:if>
	};
	function reloadRecaptcha(){
		if( typeof(Recaptcha) != 'undefined')
		{
			Recaptcha.create("{vb:raw data.publickey}", "recaptcha_div", RecaptchaOptions);
		}
	}

	if (typeof Recaptcha == 'undefined') {
		window.vBulletin = window.vBulletin || {};
		window.vBulletin.hv_script = "//www.google.com/recaptcha/api/js/recaptcha_ajax.js";
		if (typeof $ == 'undefined')
		{
			window.vBulletin.delayedInit = true;
		}
		else
		{
			$.getScript(window.vBulletin.hv_script, function() {
				reloadRecaptcha();
			});
		}
	}
	else {
		reloadRecaptcha();
	}
</script>

{vb:set conversation {vb:raw post.content}}
<vb:if condition="
	($conversation['moderatorperms']['canundeleteposts'] > 0) OR
	($conversation['moderatorperms']['canremoveposts'] > 0)
">
	{vb:set canViewSoftdeletedNode, 1}
<vb:else />
	{vb:set canViewSoftdeletedNode, 0}
</vb:if>
{vb:set showChannelInfo, 1}
<vb:if condition="$conversation['isVisitorMessage']">
	{vb:set postType, 'visitorMessage'}
	<vb:if condition="$conversation['nodeid'] != $conversation['starter']">
		{vb:set isVmComment, 1}
	</vb:if>
<vb:elseif condition="$conversation['starter'] == $conversation['nodeid']" />
	{vb:set postType, 'starter'}
	{vb:set conversation.innerPost, {vb:raw conversation.nodeid}}
<vb:elseif condition="$conversation['starter'] == $conversation['parentid']" />
	{vb:set postType, 'reply'}
<vb:else />
	{vb:set postType, 'comment'}
	{vb:set commentNodeId, {vb:raw conversation.parentid}}
	{vb:set parentConversation, {vb:raw conversation.parentConversation}}
</vb:if>

<vb:if condition="$currentNodeIsBlog">
	{vb:set nodeSchemaInfo.itemtype, http://schema.org/BlogPosting}
	{vb:set nodeType, 'blog_post'}
	<vb:if condition="isset($reportActivity) AND $reportActivity">
		{vb:set displayComments, 0}
		{vb:set userCanCommentOnThisBlog, 0}
	<vb:else />
		{vb:set displayComments, 1}
		<vb:comment>userCanCommentOnThisBlog must be set in parent widget</vb:comment>
	</vb:if>
<vb:elseif condition="$currentNodeIsArticle" />
	{vb:set nodeSchemaInfo.itemtype, http://schema.org/Article}
	{vb:set nodeType, 'article'}
	<vb:if condition="isset($reportActivity) AND $reportActivity">
		{vb:set displayComments, 0}
		{vb:set userCanCommentOnThisArticle, 0}
	<vb:else />
		{vb:set displayComments, 1}
		<vb:comment>userCanCommentOnThisArticle must be set in parent widget</vb:comment>
	</vb:if>
<vb:else />
	{vb:set nodeSchemaInfo.itemtype, http://schema.org/CreativeWork}
	{vb:set nodeType, 'topic'}
	{vb:set userCanCommentOnThisBlog, 0}
	{vb:set displayComments, 0}
</vb:if>
<li data-node-id="{vb:raw conversation.nodeid}" class="list-item{vb:raw postStatus} list-item-text h-left h-clear<vb:if condition="(!isset($reportActivity) OR !$reportActivity) AND $postType == 'starter'"> conversation-starter js-conversation-starter</vb:if><vb:if condition="$enableInlineMod OR !$conversation['approved']"> inlinemod-item</vb:if>">
	<input class="privateMessageActionCheck h-left" type="checkbox" name="node[]" value="{vb:raw conversation.nodeid}" />

	<div class="h-left list-item-wrapper">
		<div class="list-item-header h-left">
			<vb:if condition="($postType == 'visitorMessage') AND $vmDetail"><h3 class="vm-desc">{vb:rawphrase visitor_message_from_x, {vb:raw conversation.authorname}}</h3></vb:if>
			<vb:if condition="$user['showavatars']">
				{vb:set avatar, {vb:var conversation.avatar}}
				<vb:if condition="!$conversation['userid']">
					<span class="avatar h-left"><img src="{vb:raw baseurl_corecdn}/images/default/default_avatar_thumb.png" width="32" height="32" alt="{vb:rawphrase guest_avatar}" /></span>
				<vb:elseif condition="$avatar AND $avatar['avatarpath']" />
					<a href="{vb:url 'profile', {vb:raw conversation}}" class="avatar h-left avatar-{vb:raw conversation.userid}"><img src="{vb:raw baseurl_corecdn}/{vb:raw avatar.avatarpath}" alt="{vb:raw conversation.authorname}" /></a>
				</vb:if>
			</vb:if>
			<div class="post-header h-left<vb:if condition="!isset($showChannelInfo) OR !$showChannelInfo"> table center-vertical</vb:if>">
				<div class="post-action-wrapper<vb:if condition="isset($reportActivity) AND $reportActivity"> ellipsis multiline</vb:if><vb:if condition="!isset($showChannelInfo) OR !$showChannelInfo"> td</vb:if>">
					{vb:set starter.nodeid, {vb:raw conversation.starter}}
					{vb:set starter.username, {vb:raw conversation.starterauthorname}}
					{vb:set starter.userid, {vb:raw conversation.starteruserid}}

					<vb:if condition="isset($reportActivity) AND $reportActivity">
						<vb:if condition="$postType =='visitorMessage'">
							<vb:if condition="$isVmComment">
								{vb:set profileRouteInfo.userid, {vb:raw conversation.userid}}
								{vb:set profileRouteUrl, {vb:url 'profile', {vb:raw profileRouteInfo}}}
								<vb:if condition="$reportActivity">
									{vb:set vmRouteInfo.nodeid, {vb:raw conversation.starter}}
									{vb:set vmRouteInfo.title, {vb:raw conversation.starterurlident}}
									{vb:set vmRouteUrl, {vb:url 'visitormessage', {vb:raw vmRouteInfo}}}
									<vb:if condition="$conversation['userid'] > 0">
										{vb:rawphrase x_commented_on_vm_from_y, {vb:var profileRouteUrl}, {vb:raw conversation.authorname}, {vb:var vmRouteUrl}, {vb:raw conversation.starterauthorname}, {vb:raw conversation.nodeid}}
									<vb:else />
										{vb:rawphrase guest_commented_on_vm_from_y, {vb:var vmRouteUrl}, {vb:raw conversation.starterauthorname}, {vb:raw conversation.nodeid}}
									</vb:if>
								<vb:else />
									<vb:if condition="$conversation['userid'] > 0">
										{vb:rawphrase x_commented, {vb:var profileRouteUrl}, {vb:raw conversation.authorname}}
