									{vb:rawphrase {vb:raw phraseId}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw topicTitle}, {vb:var searchUrl}, {vb:var conversation.starterprefixid}, {vb:raw conversation.starterprefix_rich}}
								<vb:else />
									{vb:rawphrase {vb:raw phraseId}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw topicTitle}}
								</vb:if>
							</vb:if>
						<vb:else />
							{vb:set profileUrl, {vb:url 'profile', {vb:raw conversation}}}
							{vb:set parentProfileUrl, {vb:url 'profile', {vb:raw parentConversation}}}
							{vb:set conversationUrl, {vb:url {vb:raw conversation.starterroute}, {vb:raw starter}}}
