							{vb:set searchUrl, {vb:url 'search'}}
							{vb:set conversationTitle, {vb:raw conversation.startertitle}}
							<vb:if condition="$conversation['userid'] > 0 AND $parentConversation['userid'] > 0">
								<vb:if condition="$conversation['starterprefixid']">
									{vb:rawphrase x_commented_on_ys_reply_to_z_with_prefix, {vb:var profileUrl}, {vb:raw conversation.authorname}, {vb:var parentProfileUrl}, {vb:raw parentConversation.authorname}, {vb:var conversationExtraUrl}, {vb:raw parentConversation.nodeid}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw conversationTitle}, {vb:var searchUrl}, {vb:var conversation.starterprefixid}, {vb:raw conversation.starterprefix_rich}}
								<vb:else />
									{vb:rawphrase x_commented_on_ys_reply_to_z, {vb:var profileUrl}, {vb:raw conversation.authorname}, {vb:var parentProfileUrl}, {vb:raw parentConversation.authorname}, {vb:var conversationExtraUrl}, {vb:raw parentConversation.nodeid}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw conversationTitle}}
								</vb:if>
							<vb:elseif condition="$conversation['userid'] == 0 AND $parentConversation['userid'] > 0"/>
								<vb:if condition="$conversation['starterprefixid']">
									{vb:rawphrase guest_commented_on_ys_reply_to_z_with_prefix, {vb:var parentProfileUrl}, {vb:raw parentConversation.authorname}, {vb:var conversationExtraUrl}, {vb:raw parentConversation.nodeid}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw conversationTitle}, {vb:var searchUrl}, {vb:var conversation.starterprefixid}, {vb:raw conversation.starterprefix_rich}}
								<vb:else />
									{vb:rawphrase guest_commented_on_ys_reply_to_z, {vb:var parentProfileUrl}, {vb:raw parentConversation.authorname}, {vb:var conversationExtraUrl}, {vb:raw parentConversation.nodeid}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw conversationTitle}}
								</vb:if>
							<vb:elseif condition="$conversation['userid'] > 0 AND $parentConversation['userid'] == 0"/>
								<vb:if condition="$conversation['starterprefixid']">
									{vb:rawphrase x_commented_on_guests_reply_to_z_with_prefix, {vb:var profileUrl}, {vb:raw conversation.authorname}, {vb:var conversationExtraUrl}, {vb:raw parentConversation.nodeid}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw conversationTitle}, {vb:var searchUrl}, {vb:var conversation.starterprefixid}, {vb:raw conversation.starterprefix_rich}}
								<vb:else />
									{vb:rawphrase x_commented_on_guests_reply_to_z, {vb:var profileUrl}, {vb:raw conversation.authorname}, {vb:var conversationExtraUrl}, {vb:raw parentConversation.nodeid}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw conversationTitle}}
								</vb:if>
							<vb:else />
								<vb:if condition="$conversation['starterprefixid']">
									{vb:rawphrase guest_commented_on_guests_reply_to_z_with_prefix, {vb:var conversationExtraUrl}, {vb:raw parentConversation.nodeid}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw conversationTitle}, {vb:var searchUrl}, {vb:var conversation.starterprefixid}, {vb:raw conversation.starterprefix_rich}}
								<vb:else />
									{vb:rawphrase guest_commented_on_guests_reply_to_z, {vb:var conversationExtraUrl}, {vb:raw parentConversation.nodeid}, {vb:var conversationUrl}, {vb:raw conversation.starter}, {vb:raw conversationTitle}}
								</vb:if>
							</vb:if>
						</vb:if>
					<vb:else />
						<vb:if condition="$postType =='visitorMessage'">
							<vb:if condition="$isVmComment">
								{vb:set profileRouteInfo.userid, {vb:raw conversation.userid}}
								{vb:set profileUrl, {vb:url 'profile', {vb:raw profileRouteInfo}}}
								{vb:rawphrase x_commented, {vb:var profileUrl}, {vb:raw conversation.authorname}}
							<vb:else />
								{vb:set vm_userInfo, {vb:var conversation.vm_userInfo}}
								{vb:set profileInfo.userid, {vb:raw vm_userInfo.userid}}
								{vb:set profileUrl, {vb:url 'profile', {vb:raw profileInfo}}}
								{vb:set userInfo.userid, {vb:raw conversation.userid}}
								{vb:set userProfileUrl, {vb:url 'profile', {vb:raw userInfo}}}
								<vb:if condition="$conversation['userid'] > 0">
									{vb:rawphrase x_posted_visitormessage_for_y_2, {vb:raw conversation.authorname}, {vb:var userProfileUrl}, {vb:raw vm_userInfo.username}, {vb:var profileUrl}}
								<vb:else />
									{vb:rawphrase guest_posted_visitormessage_for_y_2, {vb:raw vm_userInfo.username}, {vb:var profileUrl}}
								</vb:if>
							</vb:if>
						<vb:elseif condition="$postType == 'starter'" />
							{vb:set conversationUrl, {vb:url {vb:raw conversation.starterroute}, {vb:raw starter}}}
							<vb:if condition="$conversation['userid'] > 0">
								{vb:set profileUrl, {vb:url 'profile', {vb:raw conversation}}}
								{vb:set phraseId, 'x_started_a_'}
								{vb:strcat phraseId, {vb:raw nodeType}}
								{vb:strcat phraseId, '_y'}
								{vb:rawphrase {vb:raw phraseId}, {vb:var profileUrl}, {vb:raw conversation.authorname}, {vb:var conversationUrl}, {vb:raw conversation.nodeid}, {vb:raw conversation.title}}
