{vb:hook profile_activity_start}
{vb:data profileInfo, user, fetchProfileInfo, {vb:raw userid}}

<vb:comment>fetch postfor info in batches</vb:comment>
{vb:set userIds, {vb:php array}}
<vb:each from="nodes" value="node">
	<vb:if condition="!empty($node['setfor'])">
		{vb:set num_elem, {vb:php array_push, {vb:raw userIds}, {vb:raw node.setfor}}}
	</vb:if>
</vb:each>
{vb:data setForInfo, user, fetchUsernames, {vb:raw userIds}, 0}

{vb:data canViewAlbum, user, hasPermissions, albumpermissions, canviewalbum}
{vb:data albumChannel, node, fetchAlbumChannel}
{vb:data isFollowing, follow, isFollowingUser, {vb:raw userid}}

<vb:each from="nodes" value="node">
	{vb:set conversation, {vb:raw node.content}}
	{vb:set contenttypeclass, {vb:raw conversation.contenttypeclass}}
	<vb:if condition="$contenttypeclass != 'PrivateMessage'">
		{vb:set showContent, 1}
		<vb:if condition="$contenttypeclass == 'Gallery'">
			<vb:if condition="isset($canViewAlbum) AND !$canViewAlbum AND ($node['parentid'] == $albumChannel)">
				{vb:set showContent, 0}
			</vb:if>
		</vb:if>
		<vb:if condition="$contenttypeclass == 'Video' AND $profileInfo['showVideos'] == 0">
			<vb:if condition="isset($isFollowing) AND $isFollowing !== 1">
				{vb:set showContent, 0}
			</vb:if>
		</vb:if>
		<vb:if condition="isset($showContent) AND $showContent > 0">
			{vb:set replyTemplateName, 'display_contenttype_conversationreply_'}
			{vb:strcat replyTemplateName, {vb:raw contenttypeclass}}

			<vb:if condition="$conversation['channeltype'] == 'blog'">
				{vb:set currentNodeIsBlog, 1}
				<vb:comment>we do not need to check for comment permissions since reportActivity=1</vb:comment>
			<vb:else />
				{vb:set currentNodeIsBlog, 0}
			</vb:if>

			<vb:if condition="$conversation['channeltype'] == 'article'">
				{vb:set currentNodeIsArticle, 1}
				<vb:comment>we do not need to check for comment permissions since reportActivity=1</vb:comment>
			<vb:else />
				{vb:set currentNodeIsArticle, 0}
			</vb:if>

			<vb:if condition="empty($node['setfor']) OR empty($setForInfo[$node['setfor']]) OR !($postfor = $setForInfo[$node['setfor']])">
				{vb:set postfor, 0}
			</vb:if>

			{vb:template {vb:raw replyTemplateName}, conversation={vb:raw conversation}, postfor={vb:raw postfor}, reportActivity = 1, showChannelInfo = 1, showInlineMod={vb:raw showInlineMod}, currentNodeIsBlog={vb:raw currentNodeIsBlog}, currentNodeIsArticle={vb:raw currentNodeIsArticle}}
		</vb:if>
	</vb:if>
</vb:each>
{vb:hook profile_activity_end}

