			</div>
	</div>
</div>

{vb:set channelid, {vb:raw $page.channelid}}
{vb:rawdata canView, user, hasPermissions, forumpermissions, canview, {vb:raw $channelid}}
{vb:rawdata canViewContent, user, hasPermissions, forumpermissions, canviewthreads, {vb:raw $channelid}}

<vb:if condition="!empty($canView) AND empty($canView['errors']) AND ($canView == true) AND !empty($canViewContent) AND empty($canViewContent['errors']) AND ($canViewContent== true)">

	<vb:if condition="empty($widgetConfig) AND !empty($widgetinstanceid)">
		{vb:data widgetConfig, widget, fetchConfig, {vb:raw widgetinstanceid}}
	</vb:if>

	<vb:if condition="isset($widgetConfig['resultsPerPage']) OR isset($user['maxposts'])">
		<vb:if condition="isset($user['maxposts']) AND $user['maxposts'] != -1">
			{vb:set resultsPerPage, {vb:raw user.maxposts}}
		<vb:else />
			{vb:set resultsPerPage, {vb:raw widgetConfig.resultsPerPage}}
		</vb:if>
	</vb:if>

	<vb:comment>This uses stream view CSS to display the starter without the block on the left for user information.</vb:comment>
	{vb:set page.view, 'stream'}

	{vb:set topicStatus, ''}
	{vb:set errorMessage, 1}

	<vb:if condition="
		!empty($page)
		AND isset($page['channelid'])
		AND $page['channelid'] > 0
		AND isset($page['nodeid'])
		AND $page['nodeid'] > 0
	">

		{vb:set nodeid, {vb:var page.nodeid}}
		{vb:data currentConversation, node, getNodeFullContent, {vb:raw nodeid}}

		<vb:if condition="!empty($currentConversation) AND empty($currentConversation['errors'])">

			{vb:set errorMessage, 0}
			{vb:set currentConversation, {vb:raw currentConversation[$nodeid]}}

			<vb:comment>Handle redirects</vb:comment>
			<vb:if condition="
				$currentConversation['contenttypeclass'] == 'Redirect'
				AND !empty($currentConversation['tonodeid'])
				AND $currentConversation['tonodeid'] != $currentConversation['nodeid']
			">
				{vb:data redirectUrl, route, getAbsoluteNodeUrl, {vb:raw currentConversation.tonodeid}}
				{vb:redirect {vb:raw redirectUrl}}
			</vb:if>

			<vb:comment>
				Since article display is based on blogs, it uses
				some of the CSS from this file. See
				blog-conversation-content-widget below
			</vb:comment>
			{vb:cssExtra css_blogs_groups.css}

			<vb:if condition="$nodeid == $currentConversation['starter']">
				{vb:set conversationStarter, {vb:raw currentConversation}}
			<vb:else />
				{vb:set starternodeid, {vb:raw currentConversation.starter}}
				{vb:data conversationStarter, node, getNodeContent, {vb:raw starternodeid}}
				{vb:set conversationStarter, {vb:raw conversationStarter[$starternodeid]}}
			</vb:if>

			<vb:if condition="isset($page['filter_time']) AND in_array($page['filter_time'], array('time_all', 'time_today', 'time_lastweek', 'time_lastmonth'))">
				{vb:set widgetConfig.url_default_filters.filter_time, {vb:raw page.filter_time}}
				<vb:if condition="$page['filter_time'] == 'time_today'">
					{vb:set options.date.from, 'lastDay'}
				<vb:elseif condition="$page['filter_time'] == 'time_lastweek'" />
					{vb:set options.date.from, 'lastWeek'}
				<vb:elseif condition="$page['filter_time'] == 'time_lastmonth'" />
					{vb:set options.date.from, 'lastMonth'}
				<vb:else />
					{vb:set options.date, 'all'}
				</vb:if>
			</vb:if>

			<vb:if condition="isset($page['filter_show']) AND in_array($page['filter_show'], array('show_all', 'vBForum_Text', 'vBForum_Gallery', 'vBForum_Video','vBForum_Link', 'vBForum_Poll'))">
				{vb:set widgetConfig.url_default_filters.filter_show, {vb:raw page.filter_show}}
				<vb:if condition="$page['filter_show'] != 'show_all'">
					{vb:set options.type, {vb:raw page.filter_show}}
				</vb:if>
			</vb:if>

			{vb:data enableInlineMod, user, hasPermissions, moderatorpermissions, canmoderateposts, {vb:var page.nodeid}}
			<vb:if condition="empty($enableInlineMod)">
				{vb:data enableInlineMod, content_channel, getCanModerate, {vb:var page.channelid}}
			</vb:if>

			{vb:set sortOption.created, 'ASC'}
			{vb:set options.depth, 1}
			{vb:set options.view, 'conversation_thread'}
			{vb:set options.nolimit, 1}
			{vb:set options.sort, {vb:raw sortOption}}
			{vb:set options.include_parent, 1}

			<vb:if condition="!empty($page['q'])">
				{vb:set options.keywords, {vb:raw page.q}}
			</vb:if>

			{vb:set options.channel, {vb:raw nodeid}}
			{vb:set options.include_starter, 1}
			{vb:set options.withParent, 0}
			{vb:set options.ignore_protected, 1}

			{vb:data nodes, search, getInitialResults, {vb:raw options}, {vb:raw resultsPerPage}, 1, 1}
			{vb:set totalCount, {vb:raw nodes.totalRecords}}
			{vb:set nodes, {vb:raw nodes.results}}

			<vb:comment>Add view counts for articles</vb:comment>
			{vb:data nodes, node, 'mergeNodeviewsForTopics', {vb:raw nodes}}

			{vb:set modPerms, {vb:raw currentConversation.moderatorperms}}
			<vb:each from="nodes" value="node" key="index">
				<vb:if condition="!empty($node['content']['moderatorperms'])">
					{vb:set openStatus, {vb:raw node.open}}
					{vb:set content, {vb:raw node.content}}
					{vb:action modPerms, permission, actionMergePerms, {vb:raw modPerms}, {vb:raw content.moderatorperms}}
				</vb:if>
				{vb:set nodes.$index.content.can_use_multiquote, 0}
			</vb:each>

			<vb:each from="modPerms" value="perm">
				<vb:if condition="$perm > 0">
					{vb:set enableInlineMod, 1}
				</vb:if>
			</vb:each>

			<vb:if condition="$currentConversation['showopen'] == 0 AND !$currentConversation['canmoderate'] AND $currentConversation['open'] == 0">
				{vb:strcat topicStatus, ' closed'}
			</vb:if>
			<vb:if condition="($currentConversation['showopen'] == 0) AND ($currentConversation['canmoderate'] == 0)">
				{vb:strcat topicStatus, ' closed_content-entry-box'}
			</vb:if>
			<vb:if condition="$currentConversation['sticky']">
				{vb:strcat topicStatus, ' sticky'}
			</vb:if>
			<vb:if condition="$currentConversation['approved'] == 0">
				{vb:strcat topicStatus, ' unapproved'}
			</vb:if>
			<vb:if condition="$currentConversation['featured']">
				{vb:strcat topicStatus, ' featured'}
			</vb:if>
			<vb:if condition="$currentConversation['unpublishdate']">
				{vb:strcat topicStatus, ' deleted'}
			</vb:if>

		</vb:if>
	</vb:if>

	{vb:set showFilter, 0}
	{vb:set showSearch, 0}

	<vb:comment>allow commenting only if the user can reply and the article is published</vb:comment>
	<vb:if condition="$currentConversation['canreply'] AND $currentConversation['showpublished']">
		{vb:set userCanCommentOnThisArticle, 1}
	<vb:else />
		{vb:set userCanCommentOnThisArticle, 0}
	</vb:if>

	<div class="b-module{vb:var widgetConfig.show_at_breakpoints_css_classes} canvas-widget default-widget conversation-content-widget blog-conversation-content-widget widget-no-border widget-no-header-buttons {vb:raw topicStatus} axd-container<vb:if condition="$currentConversation['showopen'] == 0"> showclosed</vb:if><vb:if condition="$currentConversation['showopen'] == 0 AND !$currentConversation['canmoderate'] AND $currentConversation['open'] == 0"> closed</vb:if>" id="widget_{vb:raw widgetinstanceid}" data-widget-id="{vb:raw widgetid}" data-widget-instance-id="{vb:raw widgetinstanceid}"<vb:if condition="!empty($widgetinstanceid)"> data-widget-default-tab="{vb:raw widgetConfig.default_tab}"</vb:if>>
		<vb:if condition="empty($errorMessage)">
			<div class="conversation-status-messages">
				<vb:if condition="$currentConversation['showopen'] == 0">
					<div class="conversation-status-message notice close"><span>{vb:phrase this_topic_is_closed}</span><div class="close">X</div></div>
				</vb:if>
				<vb:if condition="$currentConversation['sticky']">
					<div class="conversation-status-message notice stick"><span>{vb:phrase this_is_a_sticky_topic}</span><div class="close">X</div></div>
				</vb:if>
				<vb:if condition="$enableInlineMod">
					<vb:if condition="$currentConversation['approved'] == 0">
						<div class="conversation-status-message notice approve"><span>{vb:phrase this_topic_is_unapproved}</span><div class="close">X</div></div>
					</vb:if>
					<vb:if condition="$currentConversation['unpublishdate']">
						<div class="conversation-status-message notice delete"><span>{vb:phrase this_topic_is_deleted}</span><div class="close">X</div></div>
					</vb:if>
					<vb:if condition="$currentConversation['featured']">
						<div class="conversation-status-message notice feature"><span>{vb:phrase this_topic_has_been_featured}</span><div class="close">X</div></div>
					</vb:if>
				</vb:if>
				<div class="conversation-status-message notice h-hide"><span></span><div class="close">X</div></div>
			</div>

