								{vb:set thispagetotal, {vb:math ({vb:var thispagetotal} + 1)}}
							</vb:each>

							{vb:set postIndex, -1}
							{vb:set counter, 0}

							<vb:each from="nodes" value="node">
								<vb:if condition="$node['content']['unpublishdate']">
									{vb:set hidePostIndex, 1}
								<vb:else />
									{vb:set hidePostIndex, 0}
									{vb:set postIndex, {vb:math {vb:raw postIndex} + 1}}
								</vb:if>
