										hidePostIndex={vb:raw hidePostIndex},
										adlocation="first-post",
										pagingInfo=0}
								</vb:if>
								{vb:set counter, {vb:math ({vb:var counter} + 1)}}
							</vb:each>
						</ul>
					</div>
				</div>
			</div>
		<vb:else />
			<div class="widget-header h-clearfix">
				<div class="module-buttons">
