	<vb:each from="notices" key="noticeid" value="notice">
		{vb:set noticeNum, {vb:math {vb:raw noticeNum} + 1}}
		{vb:set notice_phrase_varname, {vb:raw notice.notice_phrase_varname}}
		<li class="notice restore<vb:if condition="$noticeNum == $noticeCount"> last-child</vb:if>" data-notice-id="{vb:raw noticeid}" data-notice-persistent="{vb:raw notice.persistent}"><vb:if condition="$user['userid'] AND $notice['dismissible']"><div class="close">X</div></vb:if>{vb:rawphrase {vb:raw notice_phrase_varname}}</li>
	</vb:each>
</ul>
</vb:if>

{vb:data subscribers, follow, getContentFollowers, {vb:raw nodeid}, {vb:raw page}, {vb:raw perpage},1}
				<ul data-page="{vb:raw page}" id="blog-subscribers-list" data-total="{vb:raw subscribers.totalcount}">
					<vb:each from="subscribers.results" value="subscriber">
							<li class="subscriber bluebox">
								<a href="{vb:url 'profile', {vb:raw subscriber}}" class="avatar h-left">
									<img src="{vb:raw baseurl_core}/image.php?userid={vb:raw subscriber.userid}&type=thumb" width="32" height="32" alt="{vb:raw subscriber.username}" title="{vb:raw subscriber.username}" />
								</a>
								<span class="author h-left">
									<a href="{vb:url 'profile', {vb:raw subscriber}}">{vb:raw subscriber.username}</a>
								</span>
								<vb:if condition="!empty($subscribers['current_userid'])">
									{vb:set buttonCSS, 'b-button--special'}
									{vb:set buttonState, ''}
									<vb:if condition="!empty($subscriber['follow_status']) AND $subscriber['follow_status'] == 2">
										{vb:set buttonType, 'subscribepending'}
										{vb:set buttonLabel, 'following_pending'}
										{vb:set buttonCSS, 'b-button--secondary'}
										{vb:set buttonState, ' disabled="disabled"'}
									<vb:elseif condition="!empty($subscriber['follow_status']) AND $subscriber['follow_status'] == 1" />
										{vb:set buttonType, 'subscribed'}
										{vb:set buttonLabel, 'following'}
										{vb:set buttonState, ' disabled="disabled"'}
									<vb:else />
										{vb:set buttonType, 'subscribe'}
										{vb:set buttonLabel, 'follow'}
									</vb:if>
									<button class="action_button {vb:raw buttonType}_button subscribe-btn b-button {vb:raw buttonCSS} h-right" data-userid="{vb:raw subscriber.userid}" id="add_{vb:raw subscriber.userid}"{vb:raw buttonState}>{vb:phrase {vb:raw buttonLabel}}</button>
								</vb:if>
								<div class="h-clear"></div>
							</li>
					</vb:each>
				</ul>
